mapper.select:
SELECT COUNT(USER_ID)
FROM PORTFOLIO
WHERE USER_ID = #{ user_id }

SELECT THUMBNAIL_IMG, TITLE, PERMIT_ST, PUBLIC_ST
FROM PORTFOLIO
WHERE USER_ID = #{ user_id }

mapper.update:
UPDATE PORTFOLIO
SET THUMBNAIL_IMG = #{ thumbnail_img }, TITLE = #{ title }, PUBLIC_ST = #{ public_st }, UPDATE_DT = SYSDATE
WHERE USER_ID = #{ user_id }
